sys_script_fix.script:
// Delete all existing records
var oldtargetGr = new GlideRecord('x_1092147_outage_l_outage_table_lv');
oldtargetGr.query();
oldtargetGr.deleteMultiple();

// Create an object to store rows from a table
var gr = new GlideRecord('cmdb_ci_outage');
gr.addQuery();
gr.query();

// 	Iterate through the existing records
while(gr.next()){
	// Populate fields in the target table with data from the source table
	var targetGr = new GlideRecord('x_1092147_outage_l_outage_table_lv');
	targetGr.initialize(); //Initialise the target record
	targetGr.number = gr.number;
	targetGr.configuration_item = gr.cmdb_ci;
	targetGr.type = gr.type;
	targetGr.begin = gr.begin;
	targetGr.end = gr.end;
	var dt1 = new GlideDateTime(gr.begin);
	var dt2 = new GlideDateTime(gr.end);
	var duration = new GlideDateTime.subtract(dt1,dt2);
	targetGr.duration = duration;

	// Insert the record into the target table
	targetGr.insert();
}
